# coding=utf-8import numpyprint('————————————————创建——————————————————————')a = numpy.array([[1, 2, 3, 4], [5, 6, 7, 8]])b = numpy.arange(8, 17)c = numpy.zeros(10, dtype='int32')d1 = numpy.ones((2,3), dtype='int32')d2 = numpy.ones_like(d1, dtype='int32')print('a', a)print('b', b)print('c', c)print('d1', d1)print('d2', d2)print('——————————————————修改类型————————————————————')a1 = a.astype('int32')print('新的', a1.dtype)print('没改', a.dtype)student = [            ('zhan', [99, 98, 87], 19),            ('lisi', [88, 99, 77], 17)           ]print('——————————————————创建复合类型——————————————————————')fixed_array = numpy.array(student, dtype=[    ('name', 'str_', 4),    ('score', 'int32', 3),    ('age', 'int32', 1)])print(fixed_array[1]['name'])print('——————————————————变维——————————————————————')a_change_style = a.reshape(1, 8)print(a_change_style, a)a_change_style2 = a.ravel()print(a_change_style2, a)a_change_style3 = a.flatten()print(a_change_style3, a)a_change_style4 = aprint(a_change_style4.shape, a.shape)a_change_style4.shape = (2, 2, 2)print(a_change_style4.shape, a.shape)a.resize([2, 4])print(a.shape)print('——————————————————切片——————————————————————')a = a.reshape([1, 8])[0]print(a)print(a[1])print(a[:5])print(a[1:5])print(a[1:])print(a[:])# print(a[0, 0])yanma = numpy.arange(1, 100, 1)print(yanma)yanma_bool = yanma % 3 == 0print(yanma[yanma_bool])